mapper.select:
SELECT *
FROM tbl_reaction
WHERE account = #{account}
AND board_no = #{bno}

mapper.delete:
DELETE FROM tbl_reaction
WHERE account = #{account}
AND board_no = #{boardNo}

mapper.update:
UPDATE tbl_reaction
SET reaction_type = #{reactionType}
WHERE account = #{account}
AND board_no = #{boardNo}
